  num? get totalVerses => _totalVerses;
  List<Verses>? get verses => _verses;
  String? get translation => _translation;
  String? get urdu => _urdu;
  String? get type => _type;
  String? get english => _english;
  String? get transliteration => _transliteration;
  String? get french => _french;
  String? get name => _name;

  Map<String, dynamic> toJson() {
    final map = <String, dynamic>{};
    map['id'] = _id;
    map['indonesian'] = _indonesian;
    map['turkish'] = _turkish;
    map['total_verses'] = _totalVerses;
    if (_verses != null) {
      map['verses'] = _verses?.map((v) => v.toJson()).toList();
    }
    map['translation'] = _translation;
